ct().height;    const svg = container.append('svg').attr('width', width).attr('height', height);    const g = svg.append('g');    // Create a D3 tree layout   const treeLayout = d3Tree().size([height, width]);    const root = d3Hierarchy(processedData);    treeLayout(root);    // Add links   const link = g.selectAll('.link')     .data(root.descendants().slice(1))     .enter()     .append('path')     .attr('class', 'link')     .attr('d', d => `M${d.y},${d.x}C${(d.y + d.parent.y) / 2},${d.x} ${(d.y + d.parent.y) / 2},${d.parent.x} ${d.parent.y},${d.parent.x}`);    // Add nodes   const node = g.selectAll('.node')     .data(root.descendants())     .enter()     .append('g')     .attr('class', 'node')     .attr('transform', d => `translate(${d.y},${d.x})`);    node.append('circle').attr('r', 5);    node.append('text')     .attr('dy', '.31em')    "
